select 
count(*)
from paylog_lot
where 1 = 1

and managerId = #{managerId,jdbcType=INTEGER}

and payTime > #{startTime,jdbcType=TIMESTAMP}
and payTime < #{endTime,jdbcType=TIMESTAMP}
