and payTime > (select IFNULL(MAX(payTime),from_unixtime(0)) from paylog_lot where managerId = #{managerId,jdbcType=INTEGER} and payType = 1 and status = 1)


select 
IFNULL(sum(money),0) 
from paylog_lot
where 1 = 1

and managerId = #{managerId,jdbcType=INTEGER}

