update.if:
		update paylog_lot

      set status = status+1


   	where managerId = #{managerId,jdbcType=INTEGER} and payType = 0 and status%10 = 0;

update paylog_lot a,manager b
   set a.status = a.status+10


where a.managerId = b.id and b.manager_up_id = #{managerId,jdbcType=INTEGER} and a.payType = 0 and a.status%100 <10;

update paylog_lot a,manager b,manager c
   set a.status = a.status+100


where a.managerId = b.id and b.manager_up_id = c.id and c.manager_up_id = #{managerId,jdbcType=INTEGER} and a.payType = 0 and c.power_id=3 and a.status%1000 <100;

update paylog_lot a,manager b,manager c,manager d
   set a.status = a.status+1000


where a.managerId = b.id and b.manager_up_id = c.id and c.manager_up_id = d.id and d.manager_up_id = #{managerId,jdbcType=INTEGER} and a.payType = 0 and d.power_id=4 and a.status <1000;
